import axios from "axios";
import { useEffect, useState } from "react";
import Image from "react-bootstrap/Image";
import Button from "react-bootstrap/Button";

export const Meme = () => {
  const memeUrl = "https://meme-api.herokuapp.com/gimme";
  const [meme, setMeme] = useState("");

  useEffect(() => {
    axios.get(memeUrl).then(response => {
      setMeme(response.data.url);
      // axios returns API response body in .data
    });
  }, []); // second param [] is a list of dependency to watch and run useEffect

  return (
    <div>
      <div className="memeViewer">
        <Image fluid='true' src={meme} alt=""></Image>
      </div>
      <div className="buttons">
        <Button variant="light">Light</Button>
        <Button variant="dark">Dark</Button>
      </div>
    </div>
  );
};
