import axios from "axios";
import { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";

export const Meme = () => {
  const memeUrl = "https://meme-api.herokuapp.com/gimme";
  const [meme, setMeme] = useState("");

  useEffect(() => {
    axios.get(memeUrl).then(response => {
      setMeme(response.data.url);
      // axios returns API response body in .data
    });
  }, []); // second param [] is a list of dependency to watch and run useEffect

  return (
    <Box
      sx={{
        width: 300,
        height: 300
      }}
    >
      <img src={meme} alt="Meme Here!!!" style={{ width: 500, height: 500 }} />

      <Button variant="outlined">
        <h1>Share</h1>
      </Button>
      <Button variant="contained">
        <h1>Next</h1>
      </Button>
    </Box>
  );
};
