import axios from "axios";
import { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import Divider from "@mui/material/Divider";
import { Link } from "@mui/material";

export const Meme = () => {
  const memeUrl = "https://meme-api.herokuapp.com/gimme";
  const [meme, setMeme] = useState("");
  const openInNewTab = meme => {
    window.open(meme, "_blank", "noopener,noreferrer");
  };

  const refreshPage = () => {
    window.location.reload(false);
  };

  useEffect(() => {
    axios.get(memeUrl).then(response => {
      setMeme(response.data.url);
      // axios returns API response body in .data
    });
  }, []); // second param [] is a list of dependency to watch and run useEffect

  return (
    <div>
      <Stack spacing={5} position="center">
        <img
          src={meme}
          alt="Meme Here!!!"
          style={{ width: 500, height: 500 }}
        />
        <Divider />
        <Button variant="contained" onClick={refreshPage}>
          Next
        </Button>
        <Button variant="outlined" onClick={() => openInNewTab(meme)}>
          Share
        </Button>
        <Divider />
        <Link
          style={{ display: "flex", justifyContent: "center" }}
          href="https://github.com/darshsoni28"
          underline="hover"
        >
          Created by - Darsh Soni
        </Link>
      </Stack>
    </div>
  );
};
