import axios from "axios";
import { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";

const memeUrl = "https://meme-api.herokuapp.com/gimme";
const [meme, setMeme] = useState("");
const openInNewTab = url => {
  window.open(meme, "_blank", "noopener,noreferrer");
};

useEffect(() => {
  axios.get(memeUrl).then(response => {
    setMeme(response.data.url);
    // axios returns API response body in .data
  });
}, []);


export const Meme = () => {
  return (
    <div>
      <Stack spacing={5} position="center">
        <img
          src={meme}
          alt="Meme Here!!!"
          style={{ width: 500, height: 500 }}
        />
        <Button variant="contained" onClick={setMeme}>
          Next
        </Button>
        <Button variant="outlined" onClick={() => openInNewTab()}>
          Share
        </Button>
      </Stack>
    </div>
  );
};
