import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";

const MemeFetch = () => {
  const [meme, setMeme] = useState("");

  const fetchMemes = async () => {
    const response = await axios("https://meme-api.herokuapp.com/gimme");
    setMeme(response.data.url);
  };

  useEffect(() => {
    fetchMemes();
  }, []);

  console.log(meme);

  return (
    <div className="flex justify-center">
      <div className="rounded overflow-hidden shadow-lg mt-32  max-w-2xl">
        <img
          className="w-full"
          src={meme}
          alt="Meme did'nt load up syke yo net sloww!!"
        />

        <div className="px-6 py-4"></div>
        <div className="flex justify-between text-6xl">
          <button>Next</button>
          <button>Share</button>
        </div>
      </div>
    </div>
  );
};

export default MemeFetch;
