import axios from "axios";
import { useEffect, useState } from "react";
export const Meme = () => {
  const memeUrl = "https://meme-api.herokuapp.com/gimme";
  const [meme, setMeme] = useState(null);

  useEffect(() => {
    axios.get(memeUrl).then(response => {
      setMeme(response.data.url);
      // axios returns API response body in .data
    });
  }, []); // second param [] is a list of dependency to watch and run useEffect


  const updateMeme = () => {
    <img src={meme} alt="Meme Here!!!" />
  }

  return (
    <div className="">
      <button onClick={updateMeme}>Next Meme</button>
    </div>
  );
};
